import React from 'react';
import './home.scss';
import 'devextreme/data/odata/store';
import DataGrid, {
  Column,
  Grouping,
  GroupPanel,
  Pager,
  Paging,
  SearchPanel,
  FilterRow,
  MasterDetail,
  Selection
} from 'devextreme-react/data-grid';
import DropDownButton from 'devextreme-react/drop-down-button';
import notify from 'devextreme/ui/notify';


var jj = require('./port_info.json');
const pageSizes = [10, 25, 50, 100];


class App extends React.Component {
  constructor(props){
    super(props);
    this.dropDataSource=["Arunan", "Mukul"];
    this.dataGridRef = React.createRef();
    this.selectedRowsData = [];

    this.getSelectedData = () => {
      this.selectedRowsData = this.dataGrid.getSelectedRowsData();
      // return this.selectedRowsData;
      return this.selectedRowsData;
    }

    this.state = {
      currentUser: "Mukul",
      allMode: 'allPages',
      checkBoxesMode: 'onClick',
      postId:1
    }
    this.onItemClick = this.onItemClick.bind(this);
    // this.setCurrentUser = this.setCurrentUser.bind(this);
    this.onSubmitClick = this.onSubmitClick.bind(this);
    // this.sendJson = this.sendJson.bind(this);
  }
  get dataGrid() {
    return this.dataGridRef.current.instance;
  }
  render() {
    return (
      <React.Fragment>
        <div>
        <div style={{marginLeft:40}}>
          <h2>Raw Data</h2>
          <DropDownButton
            text="Select user"
            icon="user"
            dropDownOptions={{ width: 230 }}
            items={this.dropDataSource}
            onItemClick={this.onItemClick}
            // onOptionChanged={this.setCurrentUser}
          />
        </div>
        <div style={{width:1080, marginLeft:40}}>
          <DataGrid
          dataSource={jj}
          allowColumnReordering={true}
          showBorders={true}
          ref={this.dataGridRef}
          >
          <FilterRow visible={true} />
          <GroupPanel visible={true} />
          <SearchPanel visible={true} highlightCaseSensitive={true} />
          <Grouping autoExpandAll={false} />

          <Selection
            mode="multiple"
            selectAllMode={this.state.allMode}
            showCheckBoxesMode={this.state.checkBoxesMode}
          />
          <Column
            dataField="port"
            caption="Port"
          />
          <Column
            dataField="ip_proto"
            caption="IP Protocol"
          />
          <Column
            dataField="service"
            caption="Service"
          />
          <Column
            dataField="description"
            caption="Description"
          />
          <Column
            dataField="source"
            caption="Source"
          />

          <Pager allowedPageSizes={pageSizes} showPageSizeSelector={true} />
          <Paging defaultPageSize={10} />

          </DataGrid>
          <button text="hi" onClick={this.onSubmitClick}/>
        </div>
      </div>
      </React.Fragment>
    );
  }
  async onSubmitClick(){
    let r = this.getSelectedData();
    let c = this.state.currentUser;
    const requestOptions = {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ rows: r, user: c })
    };
    const response = await fetch('/addTrusted', requestOptions);
    const data = await response.json();
    this.setState({ postId: data.id });
  }
  onItemClick(e){
    this.setState({
      currentUser: e.value
    });
    notify("User set to "+e.value, 'success', 5000);
  }

}

export default App;
